import React from "react";
import { TouchableHighlight, Text, StyleSheet } from "react-native";

export default function ListItem({ item }) {
    const [check, setCheck] = useState(item.check);

    const handleCheck = () => {
        setCheck(prev => !prev);
    }

    return (
        <TouchableHighlight
            onPress={handleCheck}
            activeOpacity={0.6}
            style={[styles.item, check ? styles.itemCheck : '']}
            underlayColor="#536aa5"
        >
            <Text style={styles.text}>{item.text}</Text>
        </TouchableHighlight>
    )
};

const styles = StyleSheet.create({
    item: {
        overflow: 'hidden',
        marginBottom: 16,
        borderRadius: 16,
    },
    text: {
        padding: 20,
        overflow: 'hidden',
        backgroundColor: "#fff",
        borderRadius: 16,
        color: "#000",
    }
});
