import React from "react";
import { TouchableHighlight, Text, StyleSheet } from "react-native";

export default function ListItem({ item, completeTask, index }) {
    return (
        <TouchableHighlight
            onPress={() => completeTask(index)}
            activeOpacity={0.6}
            style={styles.item}
            underlayColor="#C0C0C0"
        >
            <Text style={styles.text}>{item.text}</Text>
        </TouchableHighlight>
    )
};

const styles = StyleSheet.create({
    item: {
        overflow: 'hidden',
        marginBottom: 16,
        borderRadius: 16,
    },
    text: {
        padding: 20,
        overflow: 'hidden',
        backgroundColor: "#fff",
        borderRadius: 16,
        color: "#000",
    }
});
